import React from 'react';
import { Link } from 'react-router-dom';

import styles from './buttons.module.css'


const Buttons = (props) => {
    let template = null;

    switch (props.type) {
        case 'loadmore':
            template = (
                <div className={styles.blue_btn}

                    onClick={props.loadmore}
                >
                    {props.cta}
                </div>
            );
            break;
        case 'linkTo':
            template = (
                <Link to={props.linkTo}
                    className={styles.blur_btn}>
                                {props.cta}

                </Link>
            )
            break;
        default:
            template = null
    }
    return template;
}
export default Buttons;
